pragma solidity ^0.6.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./interfaces/IFAMToken.sol";


contract FAMToken is ERC20, IFAMToken, Ownable {
    mapping (address => bool) public _minters;
    constructor() public ERC20("Farm Token", "FAM"){
        _minters[msg.sender] = true;
    }

    /* ========== EXTERNAL MUTATIVE FUNCTIONS ========== */

    /**
     * @dev allow owner to mint
     * @param _to mint token to address
     * @param _amount amount of ALPA to mint
     */
    function mint(address _to, uint256 _amount) external override {
        require(_minters[msg.sender], "!minter");

        _mint(_to, _amount);
    }

    function addMinter(address _minter) external onlyOwner {
        _minters[_minter] = true;
    }

    function removeMinter(address _minter) external onlyOwner {
        _minters[_minter] = false;
    }
}

