pragma solidity ^0.6.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC1155/ERC1155Receiver.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";
import "@openzeppelin/contracts/utils/EnumerableSet.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

import "./interfaces/IFAMToken.sol";
import "./interfaces/IFAMAsset.sol";

contract Pool is ERC1155Receiver, ReentrancyGuard{
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    using EnumerableSet for EnumerableSet.UintSet;

    IFAMToken public token;
    IFAMAsset public asset;

    uint256 public startBlock;

    uint256 public constant REWARD_TIME = 86400;
    uint256 public constant SAFE_MULTIPLIER = 1e18;

    struct UserInfo {
        EnumerableSet.UintSet keys;
        mapping(uint256 => uint256) values;

        uint256 totalPower;

        uint256 lastReward;
        uint256 lastUpdateTime;
        uint256 lastUpdateBlock;

    }
    mapping(address => UserInfo) private userInfo;

    constructor(IFAMToken _token, IFAMAsset _asset) public {
        token = _token;
        asset = _asset;
    }

    function getUserInfo(address addr) public view returns(uint256, uint256, uint256, uint256){
        return(userInfo[addr].totalPower, userInfo[addr].lastReward, userInfo[addr].lastUpdateTime, userInfo[addr].lastUpdateBlock);
    }
    function getUserNumberAsset(address addr, uint256 assetType) public view returns(uint256){
        return userInfo[addr].values[assetType];
    }

    // todo to REVIEW!
    function stimateReward() public view returns(uint256){
        UserInfo storage _user = userInfo[msg.sender];
        uint256 secondsElaps;
        uint256 previewReward;
        secondsElaps = block.timestamp.sub(_user.lastUpdateTime);
        previewReward = _user.lastReward.add((_user.totalPower.mul(secondsElaps).mul(SAFE_MULTIPLIER)).div(REWARD_TIME));
        return previewReward;
    }

    function claim() public nonReentrant {
        UserInfo storage user = userInfo[msg.sender];
        _updateUserReward(user);
        uint256 reward = user.lastReward;
        user.lastReward = 0;
        if (reward > 0) {
            token.mint(msg.sender,reward);
        }
    }

    function retrieve(uint256[] memory _ids, uint256[] memory _values) public nonReentrant {
        require(_ids.length > 0, "Pool: invalid argument");
        require(_values.length > 0, "Pool: invalid argument");
        require(_ids.length == _values.length, "Pool: invalid argument");

        UserInfo storage user = userInfo[msg.sender];
        _updateUserReward(user);

        for (uint256 i = 0; i < _ids.length; i++) {
            uint256 id = _ids[i];
            require(_values[i]> 0 , "Pool: invalid argument");
            require(user.values[id] >= _values[i] , "Pool: invalid argument");

            user.values[id] = user.values[id].sub(_values[i]);

            if(user.values[id] == 0){
                user.keys.remove(id);
            }            
        }
        _updateTotalPower(user);
        asset.safeBatchTransferFrom(address(this), msg.sender, _ids, _values, "");

    }

    function pendingAlpa(address _user) external view returns (uint256) {

    }


    function _updateUserReward(UserInfo storage _user) private
    {

        if (block.number <= _user.lastUpdateBlock) {
            return;
        }
        uint256 secondsElaps;
        secondsElaps = block.timestamp.sub(_user.lastUpdateTime);

        // lR = lR + totalPower * secondsElaps/REWARD_TIME * SAFE_MULTIPLIER
        _user.lastReward = _user.lastReward.add((_user.totalPower.mul(secondsElaps).mul(SAFE_MULTIPLIER)).div(REWARD_TIME));
        _user.lastUpdateBlock = block.number;
        _user.lastUpdateTime = block.timestamp;
    }

    function _updateTotalPower(UserInfo storage _user) private
    {
        uint256 totalPower = 0;
        for (uint256 i = 0; i < _user.keys.length(); i++) {
            uint256 id = _user.keys.at(i);
            (, , uint32 _power) = asset.getAssetType(id);

            totalPower = totalPower.add(_user.values[id].mul(_power));
        }

        _user.totalPower = totalPower;
    }

    /* ========== ERC1155Receiver ========== */

    /**
     * @dev onERC1155Received implementation per IERC1155Receiver spec
     */
    function onERC1155Received(address _operator, address _from, uint256 _id, uint256 _value, bytes calldata _data)
    external override nonReentrant fromAssetAddress returns (bytes4) {

        UserInfo storage user = userInfo[_from];
        _updateUserReward(user);

        user.values[_id] = user.values[_id].add(_value);

        if(!user.keys.contains(_id)){
            user.keys.add(_id);
        }

        _updateTotalPower(user);

        return
            bytes4(
                keccak256(
                    "onERC1155Received(address,address,uint256,uint256,bytes)"
                )
            );

    }

    function onERC1155BatchReceived(address _operator, address _from, uint256[] calldata _ids, uint256[] calldata _values, bytes calldata _data)
    external override nonReentrant fromAssetAddress returns (bytes4) {
        require(_ids.length == _values.length, "Array must be equal!");

        UserInfo storage user = userInfo[_from];
        _updateUserReward(user);

        for (uint256 i = 0; i < _ids.length; i++) {
            uint256 id = _ids[i];
            user.values[id] = user.values[id].add(_values[i]);
            if(!user.keys.contains(id)){
                user.keys.add(id);
            }
        }

        _updateTotalPower(user);

         return
            bytes4(
                keccak256(
                    "onERC1155BatchReceived(address,address,uint256[],uint256[],bytes)"
                )
            );
    }


    /* ========== MODIFIER ========== */

    modifier fromAssetAddress() {
        require(
            msg.sender == address(asset),
            "Pool: received Asset from unauthenticated contract"
        );
        _;
    }

}
